import React, { useState } from "react";
import "./MagneticButton.css";

function MagneticButton(props) {
  const [hover, setHover] = useState(false);

  const handleMouseMove = (event) => {
    const button = event.target;
    const buttonRect = button.getBoundingClientRect();
    const centerX = buttonRect.left + buttonRect.width / 2;
    const centerY = buttonRect.top + buttonRect.height / 2;
    const deltaX = event.clientX - centerX;
    const deltaY = event.clientY - centerY;
    const distance = Math.sqrt(deltaX * deltaX + deltaY * deltaY);
    const maxDistance = Math.min(buttonRect.width, buttonRect.height) / 2;
    const scale = 1 - Math.min(distance / maxDistance, 1) * 0.2;
    const shadowX = (deltaX / distance) * 10 * scale;
    const shadowY = (deltaY / distance) * 10 * scale;
    button.style.setProperty("--shadow-x", `${shadowX}px`);
    button.style.setProperty("--shadow-y", `${shadowY}px`);
    setHover(true);
  };

  const handleMouseOut = (event) => {
    const button = event.target;
    button.style.setProperty("--shadow-x", "0px");
    button.style.setProperty("--shadow-y", "0px");
    setHover(false);
  };

  return (
    <button
      className={`magnetic-button ${hover ? "hover" : ""}`}
      onMouseMove={handleMouseMove}
      onMouseOut={handleMouseOut}
    >
      {props.children}
    </button>
  );
}

function Contact() {
  return (
    <div className="contacts">
      <MagneticButton>Click Me</MagneticButton>
    </div>
  );
}

export default Contact;
