import React, { useRef, useEffect } from "react";
import "./home.css";

function Home() {
  const img1Ref = useRef(null);
  const img2Ref = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      let val = window.scrollY;
      img1Ref.current.style.top = val * 0.6 + "px";
      img2Ref.current.style.top = val * 0.6 * 0.94 + "px";
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div>
      <header className="head-container">
        <div className="nav-box">
          <nav className="nav-left">
            <a href="#" className="logo">
              Shubham
            </a>
          </nav>
          <nav className="nav-right">
            <a href="#" className="nav-link">
              Home
            </a>
            <a href="#" className="nav-link">
              Project
            </a>
            <a href="#" className="nav-link">
              About Me
            </a>
            <a href="#" className="nav-link">
              Contacts
            </a>
          </nav>
        </div>
      </header>
      <div className="parallax-img">
        <img src={require("../accets/img/1.png")} id="img1" alt="" />
        <img src={require("../accets/img/2.png")} id="img2" alt="" />
        <img src={require("../accets/img/3.png")} id="img3" alt="" />
        {/* <span id="img1"></span>
        <span id="img2"></span>
        <span id="img3"></span> */}
      </div>
      <div className="content-container"></div>
    </div>
  );
}

export default Home;
