import React, { useState, useEffect } from "react";
import "./MagneticButtons.css";

const Contact = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [distance, setDistance] = useState(100);

  const handleMouseMove = (event) => {
    const mouseX = event.clientX;
    const mouseY = event.clientY;
    setPosition({ x: mouseX, y: mouseY });
  };

  const handleMouseOut = () => {
    setPosition({ x: 0, y: 0 });
  };

  useEffect(() => {
    const buttons = document.querySelectorAll(".magnetic-button");

    buttons.forEach((button) => {
      const buttonRect = button.getBoundingClientRect();
      const buttonCenterX = buttonRect.left + buttonRect.width / 2;
      const buttonCenterY = buttonRect.top + buttonRect.height / 2;

      const distanceX = Math.abs(position.x - buttonCenterX);
      const distanceY = Math.abs(position.y - buttonCenterY);

      const distance = Math.sqrt(distanceX ** 2 + distanceY ** 2);

      if (distance < 200) {
        button.style.transform = `translate(${
          (position.x - buttonCenterX) / 10
        }px, ${(position.y - buttonCenterY) / 10}px)`;
      } else {
        button.style.transform = "";
      }
    });
  }, [position]);

  return (
    <div
      className="magnetic-buttons-container"
      onMouseMove={handleMouseMove}
      onMouseOut={handleMouseOut}
    >
      <button
        className="magnetic-button"
        style={{ left: "100px", top: "50px" }}
      >
        Button 1
      </button>
      <button
        className="magnetic-button"
        style={{ left: "250px", top: "100px" }}
      >
        Button 2
      </button>
      <button
        className="magnetic-button"
        style={{ left: "400px", top: "50px" }}
      >
        Button 3
      </button>
    </div>
  );
};

export default Contact;
