import React, { useRef, useEffect } from "react";
import "./home.css";

function Home() {
  const img1Ref = useRef(null);
  const img2Ref = useRef(null);
  const img3Ref = useRef(null);
  const img4Ref = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      let val = window.scrollY;
      img1Ref.current.style.top = -val * 1 + "px";
      img2Ref.current.style.top = -val * 1.1 + "px";
      //   img3Ref.current.style.top = -val * 1.9 + "px";
      //   img4Ref.current.style.height = val * 0.84 + "px";
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div>
      <header className="head-container">
        <div className="nav-box">
          <nav className="nav-left">
            <a href="#" className="logo">
              Shubham
            </a>
          </nav>
          <nav className="nav-right">
            <a href="#" className="nav-link">
              Home
            </a>
            <a href="#" className="nav-link">
              Project
            </a>
            <a href="#" className="nav-link">
              About Me
            </a>
            <a href="#" className="nav-link">
              Contacts
            </a>
          </nav>
        </div>
      </header>
      <div className="parallax-img">
        <img
          src={require("../accets/img/1.png")}
          className="img"
          ref={img1Ref}
          alt=""
        />
        <img
          src={require("../accets/img/2.png")}
          className="img"
          ref={img2Ref}
          alt=""
        />
        <div className="img img3" ref={img3Ref}>
          <img
            src={require("../accets/img/3.png")}
            style={{ width: "100%" }}
            alt=""
          />
          <span className="img3-bor" ref={img4Ref}></span>
        </div>

        {/* <span id="img1"></span>
        <span id="img2"></span>
    <span id="img3"></span> */}
      </div>
      {/* <img
        src={require("../accets/img/3.png")}
        
        
        alt=""
      /> */}
      <div className="content-container" style={{ position: "relative" }}>
        <p>
          Lorem ipsum dolor, sit amet consectetur adipisicing elit. Cupiditate
          quas eligendi eum! Eum exercitationem delectus a, ad libero nulla
          consequatur ipsum eligendi natus et non aliquid aspernatur debitis
          harum vitae.
        </p>
      </div>
    </div>
  );
}

export default Home;
